sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( newValue == '') {
			return;
		}

		var whatneeded = g_form.getValue('sys_class_name');


		// Clear all of the choices from the What needed field choice list
		g_form.clearOptions('sys_class_name');

		// If the the value of the Request type field is hr, add
		// two hr choices and other to the What needed field choice list
		if(newValue == 'hr'){
			g_form.addOption('sys_class_name','hr1','Human Resources 1');
			g_form.addOption('sys_class_name','hr2','Human Resources 2');
			g_form.addOption('sys_class_name','other','Other');
		}
		// If the the value of the Request type field is facilities, add
		// two facilities choices and other to the What needed field
		// choice list
		if(newValue == 'facilities'){
			g_form.addOption('sys_class_name','facilities1','Facilities 1');
			g_form.addOption('sys_class_name','facilities2','Facilities 2');
			g_form.addOption('sys_class_name','other','Other');
		}
		// If the the value of the Request type field is legal, add
		// two legal choices and other to the What needed field
		// choice list
		if(newValue == 'NeedIt'){
			g_form.addOption('sys_class_name','legal1','Legal 1');
			g_form.addOption('sys_class_name','legal2','Legal 2');
			g_form.addOption('sys_class_name','other','Other');
		}
jslog("ME:  The value of x = " + x);
		// If the form is loading and it is not a new record, set the sys_class_name value to the
		// value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('sys_class_name', whatneeded);
		}
	}
